@import "@/assets/sass/imports.scss";

.n-menu {
  background-color: white;
  border: rem(1px) solid rgba(black, 0.08);
  font-family: $font-stack;
  list-style: none;
  margin: 0;
  max-height: 100vh;
  max-width: rem(272px);
  min-width: rem(168px);
  outline: none;
  overflow-x: hidden;
  overflow-y: auto;
  padding: rem(4px 0);

  &.is-raised {
    border: none;
    box-shadow: 0 2px 4px -1px rgba(black, 0.2), 0 4px 5px 0 rgba(black, 0.14),
      0 1px 10px 0 rgba(black, 0.12);
  }

  &.has-secondary-text {
    min-width: rem(240px);
    max-width: rem(304px);
  }
}
