export default function (examples) {
  Array.isArray(examples) && examples.forEach(function (ex) {
    if (ex.type === 'code') {
      if (ex.compiled !== undefined && typeof ex.content === 'string') {
        var content = {
          raw: ex.content,
          compiled: ex.compiled
        };
        ex.content = content;
      }
    }
  });
}